import React, { Component } from 'react';
import axios from 'axios';
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      apiURL: 'http://localhost:8080/chars/search',
      name: '',
      civName: '',
      alignment: '',
      team: ''
    }

    this.handleSubmit = this.handleSubmit.bind(this);
    this.buildQuery= this.buildQuery.bind(this);

  } 



  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.name);
    event.preventDefault();
  }

  render() {
    return (
      <div className="App">

        <div className="logo">
          <h1>EXCELSIOR</h1>
          <p>The Superhero Database</p>
        </div>

        <form onSubmit={this.handleSubmit}>
          <label>
            Name:
            <input type="text" value={this.state.name} onChange={event => this.setState({ name: event.target.value })} />
          </label>
          <label>
            Civilian Name:
            <input type="text" value={this.state.civName} onChange={event => this.setState({ civName: event.target.value })} />
          </label>
          <label>
            Alignment:
            <select onChange={event => this.setState({ alignment: event.target.value })}>
              <option value="">-</option>
              <option value="Hero">Hero</option>
              <option value="Villain">Villain</option>
              <option value="Antihero">Antihero</option>
            </select>
          </label>
          <label>
            Team:
            <input type="text" value={this.state.team} onChange={event => this.setState({ team: event.target.value })} />
          </label>
        </form>

        <p>{this.state.name}</p>
        <p>{this.state.civName}</p>
        <p>{this.state.alignment}</p>
        <p>{this.state.team}</p>
        <p>{this.state.apiURL}</p>
      </div>
    );
  }
}

export default App;
