import React, { Component } from 'react';
import marvelLogo from'./marvel-logo.png';
import DCLogo from'./DC-logo.png';
import imageLogo from './image-logo.png';
import valiantLogo from './valiant-logo.jpeg';
import darkHorseLogo from './dark-horse-logo.jpg';
import noImageFound from "./image-default.jpg";
import IDWLogo from './IDW-logo.png';
import "./ResultsPage.css";

class ResultsPage extends Component {

    constructor(props) {
        super(props);
    }

    render() {

        function cardStylePicker(company) {

            switch(company) {
                case "Marvel":
                    return "card marvel";
                    break;
                case "DC":
                    return "card dc";
                    break;
                default:
                    return "card indy";
            }

        }

        function logoPicker(company) {

            switch(company) {
                case "Marvel":
                    return marvelLogo;
                    break;
                case "DC":
                    return DCLogo;
                    break;
                case "Image":
                    return imageLogo;
                    break;
                case "Valiant":
                    return valiantLogo;
                    break;
                case "Dark Horse":
                    return darkHorseLogo;
                    break;
                case "IDW":
                    return IDWLogo;
                    break;   
                default:
                    return "";
            }

        }

        if(this.props.searchedOnce) {
            if (this.props.searchResults.length > 0) {
                return (

                    <div className="container">

                        {   
                            this.props.searchResults.map((char) => {
                                return (
                                    <div className="col s5">
                                        <div className={cardStylePicker(char.company)}>
                                        <div className="row">
                                            <div className="col s6">
                                                <img className="responsive-img charImage" src={(char.imageURL) ? char.imageURL : noImageFound} />
                                                <p ><i>{char.desc}</i></p>
                                            </div>
                                            <div className="col s6">
                                                <h3>{char.name.toUpperCase()}</h3>
                                                <div className="row">
                                                    <div className="col s6">
                                                        <b>Name: </b>
                                                        <p>{char.civName}</p>
                                                    </div>
                                                    <div className="col s6">
                                                        <b>Debut: </b>
                                                        <p>{char.firstDebut}</p>
                                                    </div>
                                                </div>                                   
                                                <div className="row">
                                                    <div className="col s6">
                                                        <h2>ABILITIES</h2>
                                                        <ul className="abilities-list">
                                                        {char.abilities.map(ability => {
                                                            return (<li>{ability}</li>)
                                                        })}
                                                        </ul>
                                                    </div>
                                                    <div className="col s6">
                                                        <h2>TEAMS</h2>
                                                        <ul>
                                                        {char.teams.map(team => {
                                                            return (<li>{team}</li>)
                                                        })}
                                                        </ul>
                                                    </div >
                                                </div>
                                            </div>

                                            <div>

                                                <div>
                                                    <img className="company-logo" src={logoPicker(char.company)} alt="Logo"/>         
                                                </div>                   
                                            </div> 
                                        </div>                           
                                    </div>
                                    </div>

                                )
                            })
                        }    
                    </div>     
                )
            }
            else {
                return (
                    <div><h1 className="sorry-message full-area circuit-background">Sorry, but your search didn't find any results.</h1></div>
                )
            }
        }
        else {
            return (
                <div className="full-area circuit-background"></div>
            )
        }


    }
}

export default ResultsPage;
