import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

import axios from 'axios';
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      apiURL: 'http://localhost:8080/chars/search?',
      name: '',
      civName: '',
      alignment: '',
      team: '',
      ability: '',

    }

    this.handleSubmit = this.handleSubmit.bind(this);
    this.multiQueryChecker = this.multiQueryChecker.bind(this);
    this.buildQuery = this.buildQuery.bind(this);

  }

  buildQuery() {

    let searchQuery = this.state.apiURL;
    let queryStrArr = [];

    if (this.state.name) {
      queryStrArr.push(`name=${this.state.name}`);
    }

    if (this.state.alignment) {
      queryStrArr.push(`alignment=${this.state.alignment}`);
    }

    if (this.state.team) {
      queryStrArr.push(`team=${this.state.team}`);
    }

    if (this.state.ability) {
      queryStrArr.push(`ability=${this.state.ability}`);
    }

    for (let i = 0; i < queryStrArr.length; i++) {
      searchQuery += queryStrArr[i];
      if  (i < queryStrArr.length -1) {
        searchQuery += '&';
      }
      console.log(searchQuery); 
    }

    console.log(`we're the search query is ${searchQuery}!`);
    this.setState({apiURL: searchQuery},() => {
      alert(`A url was submitted: ${this.state.apiURL}`);
    });
  }

  handleSubmit(event) {
    console.log(`we're building the query!`);
    this.buildQuery();
    event.preventDefault();
  }

  render() {
    return (
      <Router>
        <div>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/about">About</Link>
            </li>
            <li>
              <Link to="/topics">Topics</Link>
            </li>
          </ul>

          <hr />

          <Route exact path="/" component={Home} />

        </div>
      </Router>
    );
  }
}

export default App;
