import React, { Component } from 'react';
import axios from 'axios';
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      apiURL: 'http://localhost:8080/chars/search?',
      name: '',
      civName: '',
      alignment: '',
      team: '',
      ability: '',
      multiQuery: false
    }

    this.handleSubmit = this.handleSubmit.bind(this);
    this.multiQueryChecker = this.multiQueryChecker.bind(this);
    this.buildQuery = this.buildQuery.bind(this);

  } 

  multiQueryChecker() {
    if (this.state.name || this.state.alignment || this.state.team || this.state.ability ) {
      this.setState({multiQuery: true});
      this.setState({apiURL: `${this.state.apiURL}&` });
      console.log(this.state.apiURL);
    }
  }

  buildQuery() {

    let paramsArr = ['name', 'alignment', 'team', 'ability'];

    paramsArr.forEach(param => {
      if (this.state.param) {
        console.log(this.state.param);
        this.setState({apiURL: `${this.state.apiURL}${param}=${this.state.param}`}, () => {
          this.multiQueryChecker();
         });

      }

    });



    // if (this.state.name) {
    //   console.log(this.state.name);
    //   this.setState({apiURL: `${this.state.apiURL}name=${this.state.name}`}, () => {
    //     this.multiQueryChecker();
    //    });

    // }
    // if (this.state.alignment) {
    //   console.log(this.state.alignment);
    //   this.setState({apiURL: `${this.state.apiURL}alignment=${this.state.alignment}`}, () => {
    //     this.multiQueryChecker();
    //   });

    // }
    // if (this.state.team) {
    //   console.log(this.state.team);
    //   this.setState({apiURL: `${this.state.apiURL}team=${this.state.team}`}, () => {
    //     this.multiQueryChecker();
    //   });
    // }
    // if (this.state.ability) {
    //   console.log(this.state.ability);
    //   this.setState({apiURL: `${this.state.apiURL}ability=${this.state.ability}`}, () => {
    //     this.multiQueryChecker();
    //   });
    // }

    console.log(`we're alerting!`);

    alert(`A url was submitted: ${this.state.apiURL}`);


  }


  handleSubmit(event) {
    console.log(`we're building the query!`);
    this.buildQuery();
    event.preventDefault();
  }

  render() {
    return (
      <div className="App">

        <div className="logo">
          <h1>EXCELSIOR</h1>
          <p>The Superhero Database</p>
        </div>

        <form onSubmit={this.handleSubmit}>
          <label>
            Name:
            <input type="text" value={this.state.name} onChange={ event => this.setState({ name: event.target.value })} />
          </label>
          <label>
            Alignment:
            <select onChange={event => this.setState({ alignment: event.target.value })}>
              <option value="">-</option>
              <option value="Hero">Hero</option>
              <option value="Villain">Villain</option>
              <option value="Antihero">Antihero</option>
            </select>
          </label>
          <label>
            Team:
            <input type="text" value={this.state.team} onChange={ event => this.setState({ team: event.target.value })} />
          </label>
          <input type="submit" value="Submit" />
        </form>

        <p>{this.state.name}</p>
        <p>{this.state.alignment}</p>
        <p>{this.state.team}</p>
        <p>{this.state.apiURL}</p>
      </div>
    );
  }
}

export default App;
