import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link, } from "react-router-dom";
import { library } from '@fortawesome/fontawesome-svg-core';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import Search from './Search.js';
import AddChar from './AddChar/AddChar.js';
import UploadConfirmation from './UploadConfirmation/UploadConfirmation.js';
//import ResultsPage from './ResultsPage.js';
import './App.css';


class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      authorized: false
    }

    this.handleLogin = this.handleLogin.bind(this);
    this.handleLogout = this.handleLogout.bind(this);
  }

  handleLogin(event) {
    this.setState({ authorized: true });
    // event.preventDefault();
  }

  handleLogout(event) {
    this.setState({authorized: false});
    // event.preventDefault();
  }

  render() {
    return (
      <Router>
        <div>
          <div className="app">          
            <div className="portfolioBar">
              <div className="arrowContainer">
                <a href="#"><i className="fas fa-arrow-left fa-3x"></i></a>
              </div>
            </div>  
          </div>
      <Route 
        exact path='/' render={(props) => 
         <Search isAuthed={this.state.authorized} handleLogin={this.handleLogin} {...props} />
      }
      />
      <Route 
        exact path='/upload' render={(props) => 
         <AddChar isAuthed={this.state.authorized} {...props} />
      }
      />
      <Route 
        exact path='/upload/success' render={(props) => 
         <UploadConfirmation isAuthed={this.state.authorized} {...props} />
        }
      />
        </div>
      </Router>
    );
  }
}

export default App;
